import React, { useState } from "react";
import Step0 from "./Step0";
import Step1 from "./Step1";

const AppTest: React.FC = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [step0Valid, setStep0Valid] = useState(false);
  const [step1Valid, setStep1Valid] = useState(true);
  const [views, setViews] = useState<Array<JSX.Element>>([]);

  const handleNextStep = () => {
    if (currentStep === 0) {
      setStep0Valid(true);
      setCurrentStep(1);
    }
  };

  const handleAddView = () => {
    if (step1Valid) {
      const view = (
        <div key={views.length}>
          <h2>View{views.length}</h2>
          {/* Content for additional views */}
        </div>
      );
      setViews([...views, view]);
    }
  };

  const handleFormSubmit = () => {
    // Validate all required fields in Step0 and Step1
    setStep0Valid(true);
    setStep1Valid(true);
    // Perform other actions on form submit
    if (step0Valid && step1Valid) {
      alert("Thành công");
    } else {
      alert(
        "Vui lòng điền đủ thông tin và thực hiện validation với các trường bắt buộc nhập"
      );
    }
  };

  return (
    <div>
      {currentStep === 0 && <Step0 onNextStep={handleNextStep} />}
      {currentStep === 1 && (
        <Step1 onAddView={handleAddView} isValid={step0Valid && step1Valid} />
      )}
      {views.map((view) => view)}
      <button onClick={handleFormSubmit}>Submit</button>
    </div>
  );
};

export default AppTest;
